RELAY_ADDRESS=0xa0Ee7A142d267C1f36714E4a8F75612F20a79720
RELAY_PRIVATE_KEY=0x2a871d0798f97d79848a013d4936a73bf4cc922c825d33c1cf7073dff6d409c6
RPC_URL=http://localhost:8545

# Check if state already exists.
if [ -f /app/shared/anvil.json ]; then
  echo "State already initialized, skipping..."
  exit 0
fi

anvil --odyssey --host 0.0.0.0 --port 8545 --accounts 20 --dump-state /app/shared/anvil.json &

# Wait until anvil is healthy.
while ! cast block-number --rpc-url $RPC_URL > /dev/null; do
  echo "Waiting for anvil to start..."
  sleep 1
done

# Deploy contracts.
ORCHESTRATOR_ADDRESS=$(forge create Orchestrator --config-path ./account/foundry.toml --json --broadcast --rpc-url $RPC_URL --private-key $RELAY_PRIVATE_KEY --constructor-args 0x0000000000000000000000000000000000000000 | jq -r '.deployedTo')
echo "Orchestrator deployed to: $ORCHESTRATOR_ADDRESS"
ACCOUNT_ADDRESS=$(forge create IthacaAccount --config-path ./account/foundry.toml --json --broadcast --rpc-url $RPC_URL --private-key $RELAY_PRIVATE_KEY --constructor-args $ORCHESTRATOR_ADDRESS | jq -r '.deployedTo')
echo "IthacaAccount deployed to: $ACCOUNT_ADDRESS"
ACCOUNT_PROXY_ADDRESS=$(forge create EIP7702Proxy --config-path ./account/foundry.toml --json --broadcast --rpc-url $RPC_URL --private-key $RELAY_PRIVATE_KEY --constructor-args $ACCOUNT_ADDRESS $RELAY_ADDRESS | jq -r '.deployedTo')
echo "EIP7702Proxy deployed to: $ACCOUNT_PROXY_ADDRESS"
SIMULATOR_ADDRESS=$(forge create Simulator --config-path ./account/foundry.toml --json --broadcast --rpc-url $RPC_URL --private-key $RELAY_PRIVATE_KEY | jq -r '.deployedTo')
echo "Simulator deployed to: $SIMULATOR_ADDRESS"
FUNDER_ADDRESS=$(forge create SimpleFunder --config-path ./account/foundry.toml --json --broadcast --rpc-url $RPC_URL --private-key $RELAY_PRIVATE_KEY --constructor-args $RELAY_ADDRESS $ORCHESTRATOR_ADDRESS $RELAY_ADDRESS | jq -r '.deployedTo')
echo "Funder deployed to: $FUNDER_ADDRESS"
ESCROW_ADDRESS=$(forge create Escrow --config-path ./account/foundry.toml --json --broadcast --rpc-url $RPC_URL --private-key $RELAY_PRIVATE_KEY | jq -r '.deployedTo')
echo "Escrow deployed to: $ESCROW_ADDRESS"
SETTLER_ADDRESS=$(forge create SimpleSettler --config-path ./account/foundry.toml --json --broadcast --rpc-url $RPC_URL --private-key $RELAY_PRIVATE_KEY --constructor-args $RELAY_ADDRESS | jq -r '.deployedTo')
echo "Settler deployed to: $SETTLER_ADDRESS"
EXP1_ADDRESS=$(forge create ExperimentERC20 --config-path ./demo/foundry.toml --json --broadcast --rpc-url $RPC_URL --private-key $RELAY_PRIVATE_KEY --constructor-args "ExperimentERC20" "EXP" 1ether | jq -r '.deployedTo')
echo "EXP1 deployed to: $EXP1_ADDRESS"
EXP2_ADDRESS=$(forge create ExperimentERC20 --config-path ./demo/foundry.toml --json --broadcast --rpc-url $RPC_URL --private-key $RELAY_PRIVATE_KEY --constructor-args "ExperimentERC20" "EXP2" 10ether | jq -r '.deployedTo')
echo "EXP2 deployed to: $EXP2_ADDRESS"

# Deploy Multicall contract.
MULTICALL_ADDRESS=0xcA11bde05977b3631167028862bE2a173976CA11
MULTICALL_
cast rpc --rpc-url $RPC_URL anvil_setCode $MULTICALL_ADDRESS $MULTICALL_CODE
echo "Multicall deployed to: $MULTICALL_ADDRESS"

# Fund funder.
FUNDER_BALANCE=$(cast to-wei 10000 | cast to-hex)
cast rpc --rpc-url $RPC_URL anvil_setBalance $FUNDER_ADDRESS $FUNDER_BALANCE
cast send --rpc-url $RPC_URL --private-key $RELAY_PRIVATE_KEY $EXP1_ADDRESS 'mint(address,uint256)' $FUNDER_ADDRESS $FUNDER_BALANCE
cast send --rpc-url $RPC_URL --private-key $RELAY_PRIVATE_KEY $EXP2_ADDRESS 'mint(address,uint256)' $FUNDER_ADDRESS $FUNDER_BALANCE
echo "Funder ($FUNDER_ADDRESS) funded."

# Set gas wallets for funder.
GAS_WALLET_ADDRESSES="[$(for i in {0..19}; do cast wallet address --mnemonic "test test test test test test test test test test test junk" --mnemonic-index $i; done | tr '\n' ',' | sed 's/,$//')]"
cast send --rpc-url $RPC_URL --private-key $RELAY_PRIVATE_KEY $FUNDER_ADDRESS 'setGasWallet(address[],bool)' "$GAS_WALLET_ADDRESSES" true
echo "Gas wallets set."

# Kill anvil.
kill $(pgrep anvil)

# Copy relay.yaml to shared directory.
cp /app/relay.yaml /app/shared/relay.yaml

# Update relay.yaml.
yq -i ".orchestrator = \"$ORCHESTRATOR_ADDRESS\"" /app/shared/relay.yaml
yq -i ".delegation_proxy = \"$ACCOUNT_PROXY_ADDRESS\"" /app/shared/relay.yaml
yq -i ".simulator = \"$SIMULATOR_ADDRESS\"" /app/shared/relay.yaml
yq -i ".funder = \"$FUNDER_ADDRESS\"" /app/shared/relay.yaml
yq -i ".escrow = \"$ESCROW_ADDRESS\"" /app/shared/relay.yaml
yq -i ".chain.fee_tokens[1] = \"$EXP1_ADDRESS\"" /app/shared/relay.yaml
yq -i ".chain.fee_tokens[2] = \"$EXP2_ADDRESS\"" /app/shared/relay.yaml
yq -i ".chain.interop_tokens[0] = \"$EXP1_ADDRESS\"" /app/shared/relay.yaml
yq -i ".chain.interop_tokens[1] = \"$EXP2_ADDRESS\"" /app/shared/relay.yaml

# Update registry.yaml.
touch /app/shared/registry.yaml

yq -i ".31337[0].address = \"$EXP1_ADDRESS\"" /app/shared/registry.yaml
yq -i ".31337[0].kind = \"USDT\"" /app/shared/registry.yaml
yq -i ".31337[1].address = \"$EXP2_ADDRESS\"" /app/shared/registry.yaml
yq -i ".31337[1].kind = \"USDC\"" /app/shared/registry.yaml
yq -i ".31337[2].kind = \"ETH\"" /app/shared/registry.yaml
